# Unused Commitments
sum(
  clamp_min(
    max by (project_id, availability_zone) (
      limes_project_committed_per_az{
        service="compute",
        resource="cores",
        availability_zone=~"$availability_zone"  # Will not be any for compute service
      }
    )
    -
    on(project_id, availability_zone)
    max by (project_id, availability_zone) (
      limes_project_usage_per_az{
        service="compute",
        resource="cores",
        availability_zone=~"$availability_zone"
      }
    ),
    0
  )
  or
  max by (project_id, availability_zone) (
      limes_project_committed_per_az{
        service="compute",
        resource="cores",
        availability_zone=~"$availability_zone"  # Will not be any for compute service
      }
    )
)

sum(
  max by (compute_host, resource) (
    cortex_vmware_host_capacity_available{
      resource="cpu",
      availability_zone=~"$availability_zone",
      compute_host=~"$compute_host",
      workload_type="general-purpose",
      decommissioned="true",
      external_customer="false",
      pinned_project!="true"
    }
  )
) or vector(0)

